(function () {
  emailjs.init("9NTi2HpKG-pbh-USy");
})();

const contactForm = document.getElementById('contact-form');
const contactName = document.getElementById('contact-name');
const contactEmail = document.getElementById('contact-email');
const contactSubject = document.getElementById('contact-subject');
const contactMessage = document.getElementById('contact-npm install sweetalertmessage');

const clearForm = () => {
  contactName.value = '';
  contactEmail.value = '';
  contactSubject.value = '';
  contactMessage.value = '';
}

const sweet = (icon, IconColor, buttonColor, title, text) => {
  Swal.fire({
    icon: icon,
    iconColor: IconColor,
    confirmButtonColor: buttonColor,
    title: title,
    html: text
  },
  )
}

contactForm.addEventListener('submit', function (event) {
  event.preventDefault();

  const serviceID = 'service_pn8la3p';
  const templateID = 'template_a3uy0od';

  emailjs.sendForm(serviceID, templateID, this)
    .then(() => {
      sweet('success', '#1261a6', '#404756', 'Success', 'Message sent!');
      contactForm.reset();
    }, (err) => {
      btn.value = 'Send Email';
      sweet('error', '#F2522E', '#404756', 'oops...', 'Something went wrong.');
    });
});
